    .menu-btn {
        display: block;
        width: 40px;
        height: 40px;
        background-color:transparent;
        border-radius: 50%;
        position: relative;
        transition-delay: 0.2s;
        transform: scale(1.8);
    }
    .menu-btn span {
        position: absolute;
        top: 50%; margin-top: -1px;
        left: 50%; margin-left: -10px;
        width: 20px;
        height: 2px;
        background-color: #fff;

    }
    .menu-btn span::before{
        position: absolute;
        top: 50%; margin-top: -1px;
        left: 50%; margin-left: -10px;
        width: 20px;
        height: 2px;
        background-color: inherit;
    }
    .menu-btn span::after{
        position: absolute;
        top: 50%; margin-top: -1px;
        left: 50%; margin-left: -10px;
        width: 20px;
        height: 2px;
        background-color: inherit;
    }
    .menu-btn span::before,
    .menu-btn span::after {
        content: '';
        display: block;
        transition: 0.3s;
    }
    .menu-btn span::before {
        transform: translateY(-5px);
    }
    .menu-btn span::after {
        transform: translateY(5px);
    }

    .menu-btn_active span{
        height: 0;
    }
    .menu-btn_active span:before {
        transform: rotate(45deg);
    }
    .menu-btn_active span:after {
        transform: rotate(-45deg);
    }

